function SwiperAnimation() {
  this.swiper;

  this.init = function (swiper) {
    this.swiper = swiper;
    this.swiper.slides.each(function (i, el) {
      $(el).find('[data-swiper-animation]').each(function () {
        $(this).removeClass($(this).data('swiper-animation'));
        $(this).css('visibility', 'hidden');
      });
    });
    return this;
  };

  this.animate = function () {
    const active = this.swiper.slides.eq(this.swiper.activeIndex);
    active.find('[data-swiper-animation]').each(function () {
      const anim = $(this).data('swiper-animation');
      const delay = $(this).data('delay');
      const duration = $(this).data('duration');
      $(this).css('visibility', 'visible');
      if (delay) $(this).css('animation-delay', delay);
      if (duration) $(this).css('animation-duration', duration);
      $(this).addClass('animated ' + anim);
    });
  };
}

window.SwiperAnimation = SwiperAnimation;
